import React, { useState } from 'react';
import Select from 'react-select';

function AuthPage() {
    const [isLogin, setIsLogin] = useState(true);

    const toggleForm = () => {
        setIsLogin(!isLogin);
    };

    return (
        <div>
            <button onClick={toggleForm}>
                Switch to {isLogin ? "Register" : "Login"}
            </button>
            {isLogin ? <LoginForm /> : <RegisterForm />}
        </div>
    );
}

function LoginForm() {
    return (
        <form>
            <input type="email" placeholder="Email" required />
            <input type="password" placeholder="Password" required />
            <button type="submit">Login</button>
        </form>
    );
}

function RegisterForm() {
    const dietaryOptions = [
        { value: 'veg', label: 'Vegetarian' },
        { value: 'nonVeg', label: 'Non-Vegetarian' },
        { value: 'vegan', label: 'Vegan' },
        { value: 'jain', label: 'Jain' }
    ];

    const allergyOptions = [
        { value: 'nuts', label: 'Nuts' },
        { value: 'gluten', label: 'Gluten' },
        { value: 'dairy', label: 'Dairy' },
        { value: 'shellfish', label: 'Shellfish' },
        { value: 'eggs', label: 'Eggs' },
        { value: 'soy', label: 'Soy' }
        // Add more options as needed
    ];

    return (
        <form>
            <input type="text" placeholder="Name" required />
            <input type="email" placeholder="Email" required />
            <input type="password" placeholder="Password" required />
            <input type="number" placeholder="Height (cm)" required />
            <input type="number" placeholder="Weight (kg)" required />
            <Select options={dietaryOptions} placeholder="Select Dietary Preference" isClearable={true} />
            <Select options={allergyOptions} placeholder="Select Allergies (Optional)" isMulti={true} isSearchable={true} />
            <input type="date" placeholder="Date of Birth" required />
            <button type="submit">Register</button>
        </form>
    );
}

export default AuthPage;
