import React, { useState } from 'react';
import './SignUp.css';
import axios from 'axios';

const SignUp = () => {
  const [isLoginActive, setIsLoginActive] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    diagnosed: '',
    dietaryPreference: '',
    allergies: [] // Initialize allergies as an empty array
  });

  const toggleActive = () => {
    setIsLoginActive(!isLoginActive);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Function to handle adding an allergy to the allergies array
  const handleAddAllergy = (allergy) => {
    setFormData({ ...formData, allergies: [...formData.allergies, allergy] });
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/register/', formData);
      console.log('Signup successful:', response.data);
      // Handle success, e.g., redirect or display a success message
    } catch (error) {
      console.error('Signup failed:', error);
      // Handle error, e.g., display an error message
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/login/', formData);
      console.log('Login successful:', response.data);
      // Handle success, e.g., redirect or display a success message
    } catch (error) {
      console.error('Login failed:', error);
      // Handle error, e.g., display an error message
    }
  };

  return (
    <div className="signup-background">
      <div className="form-container">
        <div className="toggle-buttons">
          <button 
            className={`toggle-btn ${isLoginActive ? '' : 'active'}`} 
            onClick={() => setIsLoginActive(false)}
          >
            Signup
          </button>
          <button 
            className={`toggle-btn ${isLoginActive ? 'active' : ''}`} 
            onClick={() => setIsLoginActive(true)}
          >
            Login
          </button>
        </div>
        {isLoginActive ? (
          <form onSubmit={handleLogin}>
            {/* Login form fields */}
          </form>
        ) : (
          <form onSubmit={handleSignup}>
            {/* Signup form fields */}
            <input type="text" placeholder="Name" name="name" required onChange={handleChange} />
            <input type="email" placeholder="Email Address" name="email" required onChange={handleChange} />
            <input type="password" placeholder="Password" name="password" required onChange={handleChange} />
            <input type="number" placeholder="Age" name="age" required onChange={handleChange} />
            <input type="number" placeholder="Height (cm)" name="height" required onChange={handleChange} />
            <input type="number" placeholder="Weight (kg)" name="weight" required onChange={handleChange} />
            <select name="gender" className="custom-select" required onChange={handleChange}>
              {/* Gender options */}
            </select>
            <select name="diagnosed" required onChange={handleChange}>
              {/* Diagnosed duration options */}
            </select>
            <select name="dietaryPreference" required onChange={handleChange}>
              {/* Dietary preference options */}
            </select>
            {/* Allergies section */}
            <div>
              <label htmlFor="allergies">Allergies:</label>
              <input type="text" id="allergies" name="allergies" placeholder="Enter allergies" />
              <button type="button" onClick={() => handleAddAllergy("Nuts")}>Add Nuts</button>
              {/* Add more buttons for other common allergies */}
            </div>
            {/* Display selected allergies */}
            <div>
              <p>Selected Allergies:</p>
              <ul>
                {formData.allergies.map((allergy, index) => (
                  <li key={index}>{allergy}</li>
                ))}
              </ul>
            </div>
            {/* End of Allergies section */}
            <button type="submit" className="form-submit">Signup</button>
            <div className="form-footer">
              <a href="#" className="form-link" onClick={() => setIsLoginActive(true)}>Already have an account? Login</a>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default SignUp;
